import React, {useState, useEffect} from 'react';
import NavBarComponent from "./NavBarComponent";
import izzyFace from "../izzyface.jpg";
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import HomePageComponent from "../Components/HomePageComponent"

function UserComponent(props){
    console.log(props);
    const [events, setEvents] = useState([]);

    function handleClick() {
        var day = document.getElementById("day").value;
        var month = document.getElementById("month").value;
        var year = document.getElementById("year").value;
        var date = year + "-" + month + "-" + day;
        var event = {
            title: document.getElementById("time").value,
            date: date

        };

        if(typeof(Number(day))!== "number" || day.length !== 2){
            alert("incorrect input " + day + " for day. Please input day in two digits...i.e 07")
        }
        if(typeof(Number(month))!== "number" || month.length !== 2){
            alert("incorrect input " + month + " for month. Please input month in two digits...i.e 07")
        }
        if(typeof(Number(year))!== "number" || year.length !== 4){
            alert("incorrect input " + year + " for year. Please input year in four digits...i.e 2019")
        }
        if(typeof(Number(day))=== "number" && typeof(Number(month))=== "number" && typeof(Number(year))=== "number"
            && day.length === 2 && month.length === 2 && year.length === 4){
            setEvents([...events, event]);
            alert("Appointment Succesfully Added on " + date);
            document.getElementById("day").value = "";
            document.getElementById("month").value  = "";
            document.getElementById("year").value = "";
            document.getElementById("time").value = "";
        }

    }
if(props.loggedIn){
    return(
        <div>
            <NavBarComponent/>
            <div className="user-page">
                <div className="sideBar">
                    <div>
                        <label> Month
                            <input id="month" type="text" placeholder="XX"/>
                        </label><br/>
                        <label> Day
                            <input id="day" type="text" placeholder="XX"/>
                        </label><br/>
                        <label> Year
                            <input id="year" type="text" placeholder="XXXX"/>
                        </label><br/>
                        <label> Time
                            <input id="time" type="text" placeholder="10:00"/>
                        </label>
                    </div>
                    <button className="appointment-button" onClick={handleClick}>Add Appointment</button>
                    <div className="info-box">
                        <h2>Izzeddine Bouzid</h2> <br/>
                        <img src={izzyFace} className="facePic"/>
                        <label>email: {props.email}</label><br/>
                        <label>Status: Senior</label><br/>

                    </div>

                    <div className="info-box">
                        <h2>Courses</h2>
                        <label>COMP SCI 441</label><br/>
                        <label>MATH 220</label><br/>
                    </div>
                </div>

                <div className="calendar">
                    <FullCalendar
                        defaultView="dayGridMonth"
                        plugins={[ dayGridPlugin ]}
                        events ={events}

                    />

                </div>
            </div>




        </div>
    )
} else{
    alert("incorrect login information")
    return <HomePageComponent
        email={props.email}
        password={props.password}
        userInfo={props.userInfo}
        loggedIn={props.loggedIn}
        handleChange={props.handleChange}
        handleClick={props.handleClick}/> //<div>Username and Password not found</div>
}

}

export default UserComponent
