import React, {useState, useEffect} from 'react';
import NavBarComponent from "./NavBarComponent";
import izzyFace from "../izzyface.jpg";
import AppointmentPicker from 'appointment-picker';
import FullCalendar from '@fullcalendar/react'
import dayGridPlugin from '@fullcalendar/daygrid'

var eventArray = [];
var otherArray = [{title:"khit", date:"2019-11-11"},{title:"khit", date:"2019-11-10"},{title:"khit", date:"2019-11-09"}]

function UserComponent(props){

    const [events, setEvents] = useState([]);

    //var picker = new AppointmentPicker(document.getElementById('time'), {});

    function handleClick() {
        var day = Number(document.getElementById("day").value);
        var month = Number(document.getElementById("month").value);
        var year = Number(document.getElementById("year").value);
        var date = year + "-" + month + "-" + day;
        var event = {
            title: document.getElementById("time").value,
            date: date

        };
        alert("typeof day is " + typeof day)
        alert("typeof month is " + typeof month)
        alert("typeof year is " + typeof year)
        alert("length of day is " + day.length)
        alert("length of month is " + month.length)
        alert("length of year is " + year.length)

        if(typeof(day)!== "number" || day.length !== 2){
            alert("incorrect input " + day + " for day")
        }
        if(typeof(month)!== "number" || month.length !== 2){
            alert("incorrect input " + month + " for month")
        }
        if(typeof(year)!== "number" || year.length !== 4){
            alert("incorrect input " + year + " for year")
        }
        setEvents([...events, event]);
        console.log("this is the events");
        console.log(events);
        alert("Appointment Succesfully Added on " + date);

    }

    return(
        <div>
            <NavBarComponent/>
            <div className="user-page">
                <div className="sideBar">
                    <div>
                    <label> Month
                        <input id="month" type="text" placeholder="XX"/>
                    </label><br/>
                    <label> Day
                        <input id="day" type="text" placeholder="XX"/>
                    </label><br/>
                    <label> Year
                        <input id="year" type="text" placeholder="XXXX"/>
                    </label><br/>
                    <label> Time
                        <input id="time" type="text" placeholder="10:00"/>
                    </label>
                    </div>
                    <button className="appointment-button" onClick={handleClick}>Add Appointment</button>
                        <div className="info-box">
                            <h2>Izzeddine Bouzid</h2> <br/>
                            <img src={izzyFace} className="facePic"/>
                            <label>email: ibouzid@emich.edu</label><br/>
                            <label>Status: Senior</label><br/>

                        </div>

                        <div className="info-box">
                            <h2>Courses</h2>
                            <label>COMP SCI 441</label><br/>
                            <label>MATH 220</label><br/>
                        </div>
                </div>

                <div className="calendar">
                    <FullCalendar
                        defaultView="dayGridMonth"
                        plugins={[ dayGridPlugin ]}
                        events ={events}

                    />

                </div>
            </div>




        </div>
    )
    console.log(events);
}

export default UserComponent
