import React from "react"
import ReactDOM from 'react-dom';
import App from "./App";

class Login extends React.Component {
    constructor() {
        super()
        this.state = {
            userName: "",
            password: "",
            userInfo:{}
        }
        this.handleChange = this.handleChange.bind(this)
        this.handleClick = this.handleClick.bind(this)

    }


    handleChange(event) {
        const {name, value} = event.target
        console.log(name)
        console.log(value)
        this.setState({
            [name]: value
        })
    }
    handleClick(){
        (this.state.userName === this.state.userInfo.height && this.state.password === this.state.userInfo.mass)?
            ReactDOM.render(<App/>, document.getElementById('root')) :
            ReactDOM.render(<Login/>, document.getElementById('root'))


    }

    componentDidMount(){
            fetch("https://swapi.co/api/people/1")
                .then(response => response.json())
                .then(data => this.setState({
                    userInfo: data
                }));
    }

    render() {
        console.log(this.state.userInfo)
        console.log(this.state)
        return (
            <form>
                <input placeholder="enter username..." onChange={this.handleChange}/>
                <input placeholder="enter password..." onChange={this.handleChange}/>
                <p>{this.state.username} {this.state.password}</p>
                <button onClick={this.handleClick}>Login</button>
            </form>
        )

    }

}

export default Login;
