import React from "react"
import ReactDOM from 'react-dom';
import App from "./App";
import Register from "./Register";

class Login extends React.Component {
    constructor() {
        super()
        this.state = {
            userName: "",
            password: "",
            userInfo:{}
        }
        this.handleChange = this.handleChange.bind(this)
        this.handleClick = this.handleClick.bind(this)

    }


    handleChange(event) {
        const {name, value} = event.target
        console.log(name)
        console.log(value)
        this.setState({
            [name]: value
        })
    }
    handleClick(name) {

        if(name === "login" && this.state.userName === this.state.userInfo.height && this.state.password === this.state.userInfo.mass){

            ReactDOM.render(<App/>, document.getElementById('root'))
        }

        if(name === "register"){

            ReactDOM.render(<Register/>, document.getElementById('root'))
        }
        else{
            ReactDOM.render(<Login/>, document.getElementById('root'))
        }
    }

    componentDidMount(){
            fetch("https://swapi.co/api/people/1")
                .then(response => response.json())
                .then(data => this.setState({
                    userInfo: data
                }));
    }

    render() {

        return (
            <form>
                <input placeholder="enter username..." name="userName" onChange={this.handleChange}/>
                <input placeholder="enter password..." name="password" onChange={this.handleChange}/>
                <p>{this.state.userName} {this.state.password}</p>
                <button onClick={this.handleClick("login")}>Login</button>
                <button onClick={this.handleClick("register")}>Register</button>
            </form>
        )

    }

}

export default Login;
